### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.men_age_1549_years_who_know_that_consistent_condom_use_can_r AS DOUBLE PRECISION) ASC LIMIT 1;

