SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.women_age_1549_years_who_worked_in_the_last_12_months_and_we AS DOUBLE PRECISION) DESC LIMIT 1;

