### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.young_women_age_1829_years_who_experienced_sexual_violence_b AS DOUBLE PRECISION) DESC LIMIT 1;

