### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY h.men_age_15_years_and_above_who_use_any_kind_of_tobacco_ DESC LIMIT 1;

