### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.children_with_fever_or_symptoms_of_ari_in_the_2_weeks_preced AS DOUBLE PRECISION) DESC LIMIT 1;

