### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.children_prevalence_of_symptoms_of_acute_respiratory_infecti AS DOUBLE PRECISION) DESC LIMIT 1;

