### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.children_age_935_months_who_received_a_vitamin_a_dose_in_the AS DOUBLE PRECISION) DESC LIMIT 1;

