### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.children_age_1223_months_who_have_received_3_doses_of_polio_ AS DOUBLE PRECISION) ASC LIMIT 1;

