### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.home_births_that_were_conducted_by_skilled_health_personnel_ AS DOUBLE PRECISION) ASC LIMIT 1;

