### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.births_attended_by_skilled_health_personnel_in_the_5_years_b AS DOUBLE PRECISION) DESC LIMIT 1;

