### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.average_outofpocket_expenditure_per_delivery_in_a_public_hea AS DOUBLE PRECISION) ASC LIMIT 1;

