### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.mothers_who_consumed_iron_folic_acid_for_100_days_or_more_wh AS DOUBLE PRECISION) ASC LIMIT 1;

