### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.mothers_whose_last_birth_was_protected_against_neonatal_teta AS DOUBLE PRECISION) DESC LIMIT 1;
