### SQL
SELECT r.area_name AS state_name, (SUM(p.females) * 1000.0 / NULLIF(SUM(p.males), 0)) AS sex_ratio FROM population_stats p JOIN regions r ON p.state = r.state JOIN tru t ON p.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY sex_ratio DESC;

