### SQL
SELECT r.area_name AS state_name, ABS((SUM(CASE WHEN t.name = 'Urban' THEN p.females ELSE 0 END) * 1000.0 / NULLIF(SUM(CASE WHEN t.name = 'Urban' THEN p.males ELSE 0 END), 0)) - (SUM(CASE WHEN t.name = 'Rural' THEN p.females ELSE 0 END) * 1000.0 / NULLIF(SUM(CASE WHEN t.name = 'Rural' THEN p.males ELSE 0 END), 0))) AS sex_ratio_difference FROM population_stats p JOIN regions r ON p.state = r.state JOIN tru t ON p.tru_id = t.id GROUP BY r.area_name ORDER BY sex_ratio_difference DESC;

