### SQL
SELECT r.area_name FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id WHERE t.name='Rural' GROUP BY r.area_name HAVING SUM(p.females) > SUM(p.males);

