### SQL
SELECT r.area_name FROM population_stats p JOIN crop_stats c ON p.state=c.state JOIN regions r ON p.state=r.state GROUP BY r.area_name ORDER BY SUM(p.persons)*1.0/SUM(c.production) ASC;

