### SQL
SELECT r.area_name FROM population_stats p JOIN crop_stats c ON p.state=c.state JOIN regions r ON p.state=r.state GROUP BY r.area_name ORDER BY ABS(SUM(c.production)-SUM(p.persons)) ASC;

