### SQL
SELECT r.area_name, ABS(SUM(p.males)-SUM(p.females)) AS gender_gap FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id WHERE t.name='Total' GROUP BY r.area_name ORDER BY gender_gap ASC;

