### SQL
SELECT r.area_name, SUM(p.females)*100.0/NULLIF(SUM(p.persons),0) AS female_percentage FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id WHERE t.name='Total' GROUP BY r.area_name ORDER BY female_percentage DESC;

