### SQL
SELECT r.area_name, SUM(p.males)*100.0/NULLIF(SUM(p.persons),0) AS male_percentage FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id WHERE t.name='Total' GROUP BY r.area_name ORDER BY male_percentage DESC;

