### SQL
SELECT r.area_name, SUM(p.persons) AS working_age_population FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id WHERE t.name='Total' AND p.age NOT IN ('0-6','10-14','15-19','60+','65+') GROUP BY r.area_name ORDER BY working_age_population ASC;

