### SQL
SELECT r.area_name, SUM(p.persons)*100.0/SUM(SUM(p.persons)) OVER () AS child_share FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id WHERE t.name='Total' AND p.age='0-6' GROUP BY r.area_name ORDER BY child_share DESC;

