### SQL
SELECT r.area_name, SUM(p.persons)*1.0/SUM(SUM(p.persons)) OVER () AS urban_share FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id WHERE t.name='Urban' GROUP BY r.area_name ORDER BY urban_share ASC;

