### SQL
SELECT r.area_name, SUM(CASE WHEN p.age='0-6' THEN p.persons ELSE 0 END)*1.0/SUM(p.persons) AS child_share FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id WHERE t.name='Total' GROUP BY r.area_name ORDER BY child_share DESC;

