### SQL
SELECT r.area_name, SUM(CASE WHEN p.age IN ('60+','65+') THEN p.persons ELSE 0 END)*1.0/SUM(p.persons) AS elderly_share FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id WHERE t.name='Total' GROUP BY r.area_name ORDER BY elderly_share DESC;

