### SQL
SELECT r.area_name FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id GROUP BY r.area_name ORDER BY ABS(SUM(CASE WHEN t.name='Rural' THEN p.persons ELSE 0 END)-SUM(CASE WHEN t.name='Urban' THEN p.persons ELSE 0 END)) ASC;

